import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import ShowDesc from "./components/ShowDesc";


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      items: [
        {
          id: 1,
          title: 'Детский гранитур',
          img: 'detgar.jpg',
          price: '13999'
        },
        {
          id: 2,
          title: 'Кровать',
          img: 'krovat1.jpg',
          price: '3499'
        },
        {
          id: 3,
          title: 'Тюль белый',
          img: 'tulWhite.jpg',
          price: '1399'
        },
        {
          id: 4,
          title: 'Стол письменный',
          img: 'stolpism.jpg',
          price: '4999'
        }
      ],
      showDesc: false
    }
    this.onShowItem = this.onShowItem.bind(this)
  }

  render() {
    return (
      <div className="wrapper">
        <Header />
        <Items onShowItem={this.onShowItem} items={this.state.items}/>

        {this.state.showDesc && <ShowDesc />}
        <Footer />
      </div>
    );
  }

  onShowItem() {
    this.setState({showDesc: !this.state.showDesc})
  }
}

export default App;
