import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import ShowDesc from "./components/ShowDesc";

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      items: [
        {
          id: 1,
          title: 'Детский гранитур',
          img: 'detgar.jpg',
          img2: '',
          img3: '',
          price: '13999',
          desc: 'ОПИСАНИЕЕЕЕЕ',
        },
        {
          id: 2,
          title: 'Кровать',
          img: 'krovat1.jpg',
          img2: '',
          img3: '',
          price: '3499',
          desc: 'ОПИСАНИЕЕЕЕЕ',
        },
        {
          id: 3,
          title: 'Тюль белый',
          img: 'tulWhite.jpg',
          img2: '',
          img3: '',
          price: '1399',
          desc: 'ОПИСАНИЕЕЕЕЕ',
        },
        {
          id: 4,
          title: 'Стол письменный',
          img: 'stolpism.jpg',
          img2: '',
          img3: '',
          price: '4999',
          desc: 'ОПИСАНИЕЕЕЕЕ',
        }
      ],
      contact: [
        {
          id: 1,
          whatsapp: "8 *** ** **",
          telegram: "8 *** ** **",
          instagram: "@cherlston"
        }
      ],
      showFullItem: false,
      showContact: false,
      fullItem: {},
    }
    this.onShowContact = this.onShowContact.bind(this)
    this.onShowItem = this.onShowItem.bind(this)
  }
  render() {
    return (
      <div className="wrapper">
        <Header onShowContact={this.onShowContact} contact={this.state.contact}/>
        <Items onShowItem={this.onShowItem} items={this.state.items}/>

        {this.state.showFullItem && <ShowDesc onShowItem={this.onShowItem} item={this.state.fullItem} />}
        <Footer />
      </div>
    );
  }

  onShowContact(contact) {
    this.setState({ fullContact: contact })
    this.setState({ showContact: !this.state.showContact })
  }

  onShowItem(item) {
    this.setState({ fullItem: item })
    this.setState({ showFullItem: !this.state.showFullItem })
  }
}

export default App;
